import {LOGIN, LOGOUT, GET_USERS, CLEAR_USERS} from "../types"
import {findUsers, login, registration} from '../../api/userApi';

export const loginAction = (nickname, password, isLogin) => {
  return async dispatch => {
    const data = isLogin 
      ? await login(nickname, password) 
      : await registration(nickname, password) 
    dispatch({
      type: LOGIN, payload: data.data.token
    })
    localStorage.token = data.data.token;
  }
}

export const logout = () => {
  localStorage.clear()
  return {
    type: LOGOUT,
  }
}

export const clearUsers = () => ({type: CLEAR_USERS})

export const getUsers = nickname => {
  return async dispatch => {
    const users = await findUsers(nickname)
    dispatch({
      type: GET_USERS, payload: users.data
    })
  }
}
