import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import EmptyAvatar from '../components/EmptyAvatar/EmptyAvatar';

const Profile = () => {
  const currentUser = useSelector(state => state.user.currentUser)
  const [name, setName] = useState(currentUser?.nickname)
  console.log(currentUser);
  return (
    <div style={{ padding: '10px' }}>
      {currentUser ?
        <><label>Ваш никнейм:</label> <br />
          <input type="text" defaultValue={currentUser.nickname} value={name} onChange={e => setName(e.target.value)} /> <br /> <br />
          {currentUser.photo ?
            <img src={`${process.env.REACT_APP_API_URL}${currentUser?.photo}`} alt="photos" />
            : <EmptyAvatar big={true} nickname={currentUser.nickname} />

          }</> : null
      }

    </div>
  );
}

export default Profile;
