import React from 'react';
import {useNavigate} from 'react-router-dom'

const Login = () => {
  const navigate = useNavigate()
  const loginHandler = () => {
    localStorage.token = 'login';
    navigate('/')
  }
  return (
    <div>
      <input type="text" placeholder="Введите Никнэйм" /><br/>
      <input type="password" placeholder="Введите пароль" /><br/>
      <button onClick={loginHandler}>Войти в чат</button>
    </div>
  );
}

export default Login;
