import {LOGIN, LOGOUT, GET_USERS} from "../types"
import {login, registration} from '../../api/userApi';

export const loginAction = (nickname, password, isLogin) => {
  return async dispatch => {
    const data = isLogin 
      ? await login(nickname, password) 
      : await registration(nickname, password) 
    dispatch({
      type: LOGIN, payload: data.data.token
    })
    localStorage.token = data.data.token;
  }
}

export const logout = () => {
  localStorage.clear()
  return {
    type: LOGOUT,
  }
}

export const getUsers = nickname => {
  return async dispatch => {
    const users = []
    dispatch({
      type: GET_USERS, payload: data.data.users
    })
  }
}
