import {ADD_FRIEND, GET_FRIENDS, REMOVE_FRIEND} from "../types"
import {addFriend, removeFriend, getFriendsApi} from '../../api/friendApi';
import jwtDecode from "jwt-decode";

export const friendAddAction = friend => {
  return async dispatch => {
    try {
      await addFriend(friend)
      dispatch({
        type: ADD_FRIEND, payload: friend
      })  
    } catch (error) {
      console.log(error);
    }
    
  }
}

export const friendRemoveAction = id => {
  return async dispatch => {
    try {
      await removeFriend(id)
      dispatch({
        type: REMOVE_FRIEND, payload: id
      })  
    } catch (error) {
      console.log(error);
    }
  }
}

export const getFriends = () => {
  return async dispatch => {
    try {
      const friends = await getFriendsApi()
      dispatch({
        type: GET_FRIENDS, payload: friends
      })  
    } catch (error) {
      console.log(error);      
    }
  }
}
