import React from 'react';
import {useSelector, useDispatch} from 'react-redux'
import './friends.css'
import { friendRemoveAction } from '../../store/actions/friendAction';

const Friends = () => {
  const dispatch = useDispatch()
  const friends = useSelector(state => state.friend.friends)
  const removeFriendHandler = (event, id) => {
    event.stopPropagation()
    dispatch(friendRemoveAction(id))
  }

  const connectToFriend = event => {
    console.log('connect');
  }
  return (
    <div>
      <p>Мои Друзья</p>
      <ul>
        {friends.map(f => (
          <li key={f.id} className="item" onClick={connectToFriend}>{f.nickname} <button onClick={e => removeFriendHandler(e, f.id)}>-</button></li>
        ))}
      </ul>
    </div>
  );
}

export default Friends;
