import { LOGIN, LOGOUT } from "../types"

export const loginAction = () => {
  return async dispatch => {
    const token = 'some token' //api login
    dispatch({
      type: LOGIN, payload: token
    })
    alert('eeee')
    localStorage.token = token;
  }
}

export const logout = () => {
  localStorage.clear()
  return {
    type: LOGOUT,
  }
}
