import {$host, $authHost} from './axios'

export const registration = async (nickname, password) => {
  const response = await $host.post('api/users/registration', {nickname, password})
  return response
}

export const login = async (nickname, password) => {
  const response = await $host.post('api/users/login', {nickname, password})
  return response
}

export const findUsers = async nick => {
  const response = await $authHost.get(`api/users/${nick}`)
  return response
}
