import React, {useEffect, useState, useCallback} from 'react';
import {useSelector, useDispatch} from 'react-redux'
import { getFriends } from '../../store/actions/friendAction';
import { getUsers } from '../../store/actions/userAction';
import Findlist from '../FindList/FindList';
import Friends from '../Friends/Friends';
import './sidebar.css'

const Sidebar = ({isDrawer}) => {
  const dispatch = useDispatch()
  const [sidebar, setSidebar] = useState(['sidebar'])
  const users = useSelector(state => state.user.users)

  const friends = []

  const [nick, setNick] = useState('')

  const fetchFriends = useCallback(() => {
    dispatch(getFriends())
  }, [])

  useEffect(() => {
    fetchFriends()
  }, [dispatch]);

  useEffect(() => {
    isDrawer ? setSidebar(['sidebar', 'is_open']) : setSidebar(['sidebar'])
  }, [isDrawer])

  return (
    <div className={sidebar.join(' ')}>
      <div className="sidebar_content">
        <input 
          type="text" 
          placeholder="Найти контакт" 
          value={nick} 
          onChange={e => setNick(e.target.value)}/>
        <button onClick={() => dispatch(getUsers(nick))}>Найти</button>
        <Findlist users={users} friends={friends}/>
        <hr/>
        <Friends />
      </div>

    </div>
  );
}

export default Sidebar;
