import React, {useState} from 'react';
import {useNavigate} from 'react-router-dom'
import {useDispatch} from 'react-redux'
import { logout } from '../../store/actions/userAction';
import './chat.css'
import Header from '../Header/Header';


const Home = ({onToggle, friendConnect}) => {
  
  const dispatch = useDispatch()
  const navigate = useNavigate()

  const logoutHandler = () => {
    dispatch(logout())
    navigate('/login')
  }


  //const [connected, setConnected] = useState(false)
  const [messages, setMessages] = useState([])
  const [value, setValue] = useState('')


  const sendMessage = (room = '') => {
    // const message = {
    //   event: 'message',
    //   username: currentUser.nickname,
    //   id: Date.now(),
    //   message: value,
    //   room
    // }
    // socket.current.send(JSON.stringify(message))
    // setValue('')
  }

  const keyUpHandlerSendMessage = e => {
    if (e.code === 'Enter') {
      sendMessage()
    }
  }
  
  return (
    <div style={{flexGrow: 1}}>
      <Header onToggle={onToggle}/>
      <div className="home">
        <button onClick={logoutHandler}>Выйти из чата</button>
        <br/>
        <input type="text" value={value} onChange={e => setValue(e.target.value)} placeholder="Введите сообщение" onKeyUp={keyUpHandlerSendMessage}/>
        <button onClick={sendMessage}>Отправить сообщение</button>
        {messages.map(mess => 
          <div key={mess.id}>
            {
              mess.event === 'connection'
                ? <div>Пользователь {mess.username} подключился</div>
                : <div>{mess.username}. {mess.message}</div>
            }
          </div>  
        )}
      </div>
    </div>
  );
};



export default Home;
