import React, {useRef, useState} from 'react';
import {useNavigate} from 'react-router-dom'
import './chat.css'
import Header from './Header';


const Home = ({onToggle}) => {
  const navigate = useNavigate()
  const logoutHandler = () => {
    localStorage.token = ''
    navigate('/login')
  }

  const [connected, setConnected] = useState(false)
  const [messages, setMessages] = useState([])
  const [value, setValue] = useState('')
  const socket = useRef()

  const connection = () => {
    socket.current = new WebSocket('ws://localhost:5000')

    socket.current.onopen = () => {
      setConnected(true)
      const message = {
        event: 'connection',
        username: 'USER NumX',
        id: Date.now()
      }

      socket.current.send(JSON.stringify(message))
      console.log('Подключение установлено');
    }

    socket.current.onmessage = (event) => {
      const message = JSON.parse(event.data)
      setMessages(prev => [message, ...prev])
    }

    socket.current.onclose = () => {
      console.log('Поздключение закрыто');
    }

    socket.current.onerror = () => {
      console.log('Ошибка');
    }
  }

  const sendMessage = () => {
    const message = {
      event: 'message',
      username: 'userX',
      id: Date.now(),
      message: value
    }

    socket.current.send(JSON.stringify(message))
    setValue('')
  }
  return (
    <div style={{flexGrow: 1}}>
      <Header onToggle={onToggle}/>
      <div className="home">
        <button onClick={logoutHandler}>Выйти из чата</button>
        <button onClick={connection}>Подключиться</button>
        <br/>
        <input type="text" value={value} onChange={e => setValue(e.target.value)} placeholder="Введите сообщение" />
        <button onClick={sendMessage}>Отправить сообщение</button>
        {messages.map(mess => 
          <div key={mess.id}>
            {
              mess.event === 'connection'
                ? <div>Пользователь {mess.username} подключился</div>
                : <div>{mess.username}. {mess.message}</div>
            }
          </div>  
        )}
      </div>
    </div>
  );
};



export default Home;
