import { LOGIN, LOGOUT } from "../types";

const initialState = {
  token: null
};

const handlers = {
  [LOGIN]: (state, action) => ({
    ...state,
    token: action.payload,
  }),
  [LOGOUT]: (state) => ({
    ...state,
    token: null,
  }),
  DEFAULT: state => state
}

export const userReducer = (state = initialState, action) => {
  const handler = handlers[action.type] || handlers.DEFAULT
  return handler(state, action)
}
