import React, {useEffect} from 'react';
import {useNavigate} from 'react-router-dom'
const Chat = () => {
  const navigate = useNavigate()
  useEffect(() => {
    if (!localStorage.token) {
      navigate('/login')  
    }
  }, [navigate]);

  const logoutHandler = () => {
    localStorage.token = null
    navigate('/login')
  }
  return (
    <div>
      <h1>Ovewview chat</h1>
      <button onClick={logoutHandler}>Выйти из чата</button>
    </div>
  );
}

export default Chat;
