import React, {useEffect} from 'react';
import {useSelector, useDispatch} from 'react-redux'
import { friendAddAction } from '../../store/actions/friendAction';
import 'findlist.css'

const Findlist = ({users}) => {
  const currentUser = useSelector(state => state.user.currentUser)
  const friends = useSelector(state => state.friend.friends)
  const dispatch = useDispatch()
  const addFriend = friend => {
    dispatch(friendAddAction(friend))
  }
  useEffect(() => {
    console.log(friends);
  }, [friends]);
  return (
    <div>
      <p>Найдено {users.length} человек</p>
      <ul>
        {users.filter(u => u.id !== currentUser.id).map(user => (
          <li key={user.id}>{<img className="avatar" src={`${process.env.REACT_APP_API_URL}${user.photo}`} alt={user.photo}/>}{user.nickname} 
            { friends.some(f => f.friendId === user.id)
              ? null
              : <button onClick={() => addFriend(user)}>+</button>
            }
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Findlist;
