import React from 'react';
import {useSelector, useDispatch} from 'react-redux'
import './friends.css'
import { friendRemoveAction } from '../../store/actions/friendAction';

const Friends = ({onConnect, onlineFriends}) => {

  const dispatch = useDispatch()
  const friends = useSelector(state => state.friend.friends)

  const removeFriendHandler = (event, id) => {
    event.stopPropagation()
    dispatch(friendRemoveAction(id))
  }

  const connectToFriend = (e, nickname) => {
    e.stopPropagation()
    onConnect(nickname)
  }
  return (
    <div>
      <p>Мои Друзья</p>
      <ul>
        {friends.map(f => (
          <li key={f.id} 
            className="item" 
            onClick={(e) => connectToFriend(e, f.nickname)}>{f.nickname} {onlineFriends.some(onf => onf === f.nickname) ? 'online' : 'offline'}
            <button onClick={e => removeFriendHandler(e, f.id)}>-</button>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default Friends;
