import React, {useEffect} from 'react';
import {useNavigate} from 'react-router-dom'
import Sidebar from '../components/Sidebar';
const Chat = () => {
  const navigate = useNavigate()
  useEffect(() => {
    if (!localStorage.token) {
      navigate('/login')  
    }
  }, [navigate]);

  const logoutHandler = () => {
    localStorage.token = ''
    navigate('/login')
  }
  return (
    <div style={{display: 'flex'}}>
      <Sidebar />
      <div>
        <h1>Ovewview chat</h1>
        <button onClick={logoutHandler}>Выйти из чата</button>
      </div>
    </div>
    
  );
}

export default Chat;
