import React, {useEffect, useState} from 'react';
import {useSelector, useDispatch} from 'react-redux'
import store from '../../store';
import Findlist from '../FindList/FindList';
import Friends from '../Friends/Friends';
import './sidebar.css'

const Sidebar = ({isDrawer}) => {
  const [sidebar, setSidebar] = useState(['sidebar'])
  const users = useSelector(state => state.users)
  console.log(users);
  const [nick, setNick] = useState('')

  useEffect(() => {
    isDrawer ? setSidebar(['sidebar', 'is_open']) : setSidebar(['sidebar'])
  }, [isDrawer])
  return (
    <div className={sidebar.join(' ')}>
      <div className="sidebar_content">
        <input type="text" placeholder="Найти контакт" /><button >Найти</button>
        <Findlist />
        <hr/>
        <Friends />
      </div>

    </div>
  );
}

export default Sidebar;
