import React, {useEffect, useRef, useState} from 'react';
import './sidebar.css'

const Sidebar = ({isDrawer}) => {
  const nav = useRef()
  const [sidebar, setSidebar] = useState(['sidebar'])
  useEffect(() => {
    isDrawer ? setSidebar(['sidebar', 'is_open']) : setSidebar(['sidebar', 'is_closed'])
  }, [isDrawer])
  return (
    <div className={sidebar.join(' ')} ref={nav}>
      <div className="sidebar_content">
        <input type="text" placeholder="Найти контакт" />
        <nav>
          <ul className="contact_list">
            <li className="contact_item">Alex</li>
            <li className="contact_item">Alex Izot</li>
            <li className="contact_item">Nina</li>
            <li className="contact_item">Jenia</li>
            <li className="contact_item">Misha</li>
          </ul>
        </nav>
      </div>

    </div>
  );
}

export default Sidebar;
