import {$host, $authHost} from './axios'

export const addFriend = async ({id, nickname}) => {
  const response = await $authHost.post('api/friend', {id, nickname})
  return response
}

export const removeFriend = async id => {
  const response = await $authHost.delete('api/friend', {id})
  return response
}

export const getFriendsApi = async userId => {
  const response = await $authHost.get(`api/friend/${userId}`)
  return response
}
