import React, {useEffect, useState, useRef} from 'react';
import {useDispatch, useSelector} from 'react-redux'
import {useNavigate} from 'react-router-dom'
import Sidebar from '../components/Sidebar/Sidebar';
import Home from '../components/Home/Chat'
import { getCurrentUser } from '../store/actions/userAction';
const Chat = () => {
  const currentUser = useSelector(state => state.user.currentUser)
  const dispatch = useDispatch()
  const [isDrawer, setIsDrawer] = useState(false)
  const [room, setRoom] = useState('')
  const [friendConnect, setFriendConnect] = useState('')
  const [messages, setMessages] = useState([])
  const navigate = useNavigate()

  useEffect(() => {
    if (!localStorage.token) {
      navigate('/login')  
    }
    dispatch(getCurrentUser())
  }, [navigate, dispatch]);

  const toggleDrawerHandler = () => {
    setIsDrawer(prev => !prev)
  }

  const socket = useRef()

  const connection = (room = '') => {
    socket.current = new WebSocket('ws://localhost:5000')
    socket.current.onopen = () => {
      //setConnected(true)
      const message = {
        event: 'connection',
        username: currentUser?.nickname,
        id: Date.now(),
        room
      }
      socket.current.send(JSON.stringify(message))
      console.log('Подключение установлено');
    }

    socket.current.onmessage = (event) => {
      const message = JSON.parse(event.data)
      setMessages(prev => [message, ...prev])
    }

    socket.current.onclose = () => {
      console.log('Поздключение закрыто');
    }

    socket.current.onerror = () => {
      console.log('Ошибка');
    }
  }

  const onConnect = (nickname) => {
    setRoom(`${room}-${currentUser?.nickname}`)
    connection(nickname)
  }

  const sendMessage = (value) => {
    const message = {
      event: 'message',
      username: currentUser?.nickname,
      id: Date.now(),
      message: value,
      room
    }
    console.log(message);
    socket.current.send(JSON.stringify(message))
  }

  

  return (
    <div style={{display: 'flex'}}>
      <Sidebar isDrawer={isDrawer} onConnect={onConnect}/>
      <Home onToggle={toggleDrawerHandler} messages={messages} sendMessage={sendMessage}/>
    </div>
    
  );
}

export default Chat;
